//使用贪婪算法进行字符串匹配的程序

#include <stdio.h>
#include <stdbool.h>

//查找给定字符串和模式之间的匹配
bool stringMatching(char string[], char pattern[])
{
	//计算给定字符串的长度
	int n = strlen(string);

	//计算给定模式的长度
	int m = strlen(pattern);

	//字符串和模式的索引
	int i = 0，j = 0;

	//贪心循环匹配
	while(i < n && j < m)
	{
		if(string[i] == pattern[j])
		{
			i++;
			j++;
		}
		//在字符串中返回上一位
		else
		{
			i = i - j + 1;
			j = 0;
		}	
	}

	//检查模式是否被找到
	if(j == m)
		return true;

	return false;
}

//主程序
int main()
{
	char string[] = "ababcab";
	char pattern[] = "cab";

	bool flag = stringMatching(string, pattern);

	if (flag)
		printf("匹配模式");
	else
		printf("未匹配模式");

	return 0;
}

// 程序来实现冒泡排序算法
#include <stdio.h> 

void bubbleSort(int arr[], int n) 
{ 
	int i, j; 
	for (i = 0; i < n-1; i++)	 

	// 最后 i 个元素已经在正确的位置
	for (j = 0; j < n-i-1; j++) 
		if (arr[j] > arr[j+1]) 
			swap(&arr[j], &arr[j+1]); 
} 

void swap(int *a, int *b) 
{ 
	int temp = *a; 
	*a = *b; 
	*b = temp; 
} 

// 主程序 
int main() 
{ 
	int arr[] = {64, 34, 25, 12, 22, 11, 90}; 
